options.onRender:
// Sets the value from the first series on every refresh
const htmlgraphicsValue = htmlNode.getElementById('htmlgraphics-value');

if (htmlgraphicsValue) {
  const valueField = data.series[0]?.fields[1];
  if (valueField) {
    const length = valueField.values.length;
    htmlgraphicsValue.textContent = valueField.values.get(length - 1);
  } else {
    htmlgraphicsValue.textContent = "No data"
  }
}

options.rootCSS:
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700&display=swap');

/* ====== TOKENS ====== */
:root{
  --bg1: #061213;
  --bg2: #1c335c;

  --panel: rgba(17, 41, 75, 0.72);
  --border: rgba(255,255,255,0.08);
  --border-strong: rgba(255,255,255,0.16);

  --text: rgba(255,255,255,0.88);
  --text-muted: rgba(255,255,255,0.62);

  --radius: 18px;
  --radius-sm: 10px;

  --shadow: 0 10px 30px rgba(0,0,0,0.35);
  --shadow-soft: 0 6px 18px rgba(0,0,0,0.25);

  --focus: rgba(120, 190, 255, 0.45);

  --scale: 0.88; /* ajuste: 0.85–0.95 */
}

/*!
 * Projeto: Monitoramengo Docker
 * Autor: Marcus Rônney
 * E-mail: marcusronney@outlook.com
 * LinkedIn: https://www.linkedin.com/in/marcus-r%C3%B4nney-627657bb/
 * GitHub: https://github.com/Marcusronney
 * Atualizado em: 2026-02-10
 */


/* ====== FUNDO + TIPOGRAFIA ====== */
html, body{
  background: linear-gradient(130deg, var(--bg1), var(--bg2)) fixed;
  font-family: 'Poppins', system-ui, -apple-system, "Segoe UI", Roboto, Arial, sans-serif;
  color: var(--text);
  font-size: calc(14px * var(--scale));
  line-height: 1.35;

  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: auto;
  -moz-osx-font-smoothing: auto;
}

/* Overlay */
body::before{
  content:"";
  position: fixed;
  inset: 0;
  pointer-events: none;
  background:
    radial-gradient(900px 500px at 20% 15%, rgba(255,255,255,0.06), transparent 60%),
    radial-gradient(800px 450px at 85% 25%, rgba(80,170,255,0.10), transparent 55%),
    radial-gradient(900px 550px at 45% 85%, rgba(0,0,0,0.35), transparent 65%);
  opacity: 0.9;
}

/* ====== REMOVER BORDA EXTERNA (container do painel) ====== */
[class*="panel-container"],
[class*="panel-wrapper"],
section[aria-label="Panel"],
div[role="region"][aria-label*="Panel"]{
  border: none !important;
  box-shadow: none !important;
  outline: none !important;
  background: transparent !important;
}

/* ====== CONTEÚDO INTERNO (um único “card”) ====== */
.css-kuoxoh-panel-content,
[class*="panel-content"],
[class*="panel-body"]{
  background: var(--panel) !important;
  border: 1px solid var(--border) !important;
  border-radius: var(--radius-sm) !important;
  box-shadow: var(--shadow-soft) !important;

  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);

  transform: none !important; /* evita blur por composição */
}

/* Hover sem mover (sem translateY) */
.css-kuoxoh-panel-content:hover,
[class*="panel-content"]:hover,
[class*="panel-body"]:hover{
  border-color: var(--border-strong) !important;
  box-shadow: var(--shadow) !important;
  transition: border-color 160ms ease, box-shadow 160ms ease !important;
}

/* Evita blur “pegando” nos textos */
.css-kuoxoh-panel-content *,
[class*="panel-content"] *,
[class*="panel-body"] *{
  backdrop-filter: none !important;
  -webkit-backdrop-filter: none !important;
}

/* ====== “SEÇÕES” coloridas ====== */
.css-1bgjk0t,
.css-16lblkh,
.css-qn8k9s,
.css-12rf1df{
  background-image: linear-gradient(130deg, rgba(255,255,255,0.05), rgba(28, 51, 92, 0.65));
  border: 1px solid var(--border) !important;
  border-radius: var(--radius-sm) !important;
}

/* ====== INPUTS ====== */
input, select, textarea,
.css-1lj8xfd-input-input{
  background: rgba(10, 20, 35, 0.55) !important;
  color: var(--text) !important;

  border: 1px solid var(--border-strong) !important;
  border-radius: var(--radius-sm) !important;

  padding: 10px 12px !important;
  box-shadow: none !important;

  transition: border-color 140ms ease, box-shadow 140ms ease, background 140ms ease;
}

input::placeholder, textarea::placeholder{
  color: var(--text-muted) !important;
}

input:focus, select:focus, textarea:focus,
.css-1lj8xfd-input-input:focus{
  outline: none !important;
  border-color: rgba(120, 190, 255, 0.75) !important;
  box-shadow: 0 0 0 4px var(--focus) !important;
  background: rgba(10, 20, 35, 0.72) !important;
}

/* Links */
a{ color: rgba(140, 205, 255, 0.95); }
a:hover{ color: rgba(180, 225, 255, 1); }

